mapper.select:

SELECT COMMD_CD, COMMD_NM, COMMD_INFO, COMMD_USE
FROM C_COMMD
WHERE COMM_CD = #{commCd}

SELECT COMMD_CD, COMMD_NM
FROM   C_COMMD
WHERE  COMM_CD = #{value}

mapper.update:
UPDATE c_comm
SET    comm_nm = #{commNm},
       comm_info = #{commInfo},
       comm_use = #{commUse}
WHERE  comm_cd = #{commCd}

mapper.delete:
{CALL DELETE_COMM(#{commCd, mode=IN, jdbcType=VARCHAR, javaType=java.lang.String},
	                 #{result, mode=OUT, jdbcType=NUMERIC, javaType=int}
	                 )}

mapper.select:

SELECT * 
FROM C_PRD



prd_std = #{prdStd}



prd_un = #{prdUn}



prd_cd like '%' || Upper(#{prdCd}) || '%'



prd_nm like '%' || #{prdNm} || '%'
