mapper.select:
SELECT O.MAT_OD_CD, D.MTR_CD, C.MTR_NM, FIND_MTR_PRICE(D.MTR_CD) AS "MAT_ODD_MN", D.MAT_ODD_AMT, O.MAT_SUM, A.ACT_CD, A.ACT_NM, TO_CHAR(O.MAT_OD_DD, 'yyyy-MM-dd') AS "MAT_OD_DD", TO_CHAR(D.MAT_ODD_DATE,'yyyy-MM-dd') AS "MAT_ODD_DATE", O.MAT_OD_STS FROM M_OD O
JOIN M_ODD D ON O.MAT_OD_CD = D.MAT_OD_CD
JOIN C_MTR C ON D.MTR_CD = C.MTR_CD
JOIN C_ACT A ON D.ACT_CD = A.ACT_CD
D.MTR_CD = #{mtrCd}
AND A.ACT_NM = #{actNm}
AND O.MAT_OD_DD >= #{matInDate}
AND O.MAT_OD_DD <= #{matEdate}
ORDER BY O.MAT_OD_DD

mapper.update:
UPDATE P_PLND 
 SET PPLND_STS = 'PLN-STS2'
WHERE PPLN_CD = #{pplnCd}

mapper.select:
SELECT MAT_OD_CD, TO_CHAR(MAT_OD_DD,'YYYY-MM-DD') AS "MAT_OD_DD"
 FROM M_OD
 
    
       MAT_OD_DD >= #{matEdate}
    
    
       AND MAT_OD_DD <= #{matOdDd}

SELECT O.MAT_OD_CD, D.MTR_CD, C.MTR_NM, D.MAT_ODD_AMT, K.PA_AMT, K.ER_AMT, K.EMP_NM, TO_CHAR(K.MAT_CHK_DATE, 'yyyy-MM-dd') AS "MAT_CHK_DATE", K.ER_CD, K.ER_INFO FROM M_OD O
JOIN M_ODD D ON D.MAT_OD_CD = O.MAT_OD_CD
JOIN C_MTR C ON C.MTR_CD = D.MTR_CD
JOIN M_CHK K ON K.MAT_OD_CD = O.MAT_OD_CD
NOT ER_CD = 'ERR-MNP-006'
AND C.MTR_CD = #{mtrCd}
AND K.EMP_NM = #{empNm}
AND K.MAT_CHK_DATE >= #{matInDate}
AND K.MAT_CHK_DATE <= #{matEdate}

mapper.update:
UPDATE M_OD
SET MAT_OD_STS = 'Y'
WHERE MAT_OD_CD = #{matOdCd}

mapper.delete:
DELETE
FROM M_CHK
WHERE MAT_OD_CD = #{matOdCd}
AND ER_CD = #{erCd}

mapper.select:
SELECT I.MTR_CD, FIND_MTR_NAME(I.MTR_CD) AS "MTR_NM", FIND_ACT_NAME(I.ACT_CD) AS "ACT_NM", I.MAT_IN_AMT, I.MAT_LOT, TO_CHAR(I.MAT_IN_DATE, 'yyyy-MM-dd') AS "MAT_IN_DATE" FROM M_IN I JOIN C_ACT A ON A.ACT_CD = I.ACT_CD
I.MTR_CD = #{mtrCd}
AND A.ACT_NM = #{actNm}
AND I.MAT_IN_DATE >= #{matInDate}
AND I.MAT_IN_DATE <= #{matEdate}
ORDER BY MAT_LOT

mapper.update:
UPDATE M_CHK 
SET MAT_INSTS = 'Y'
WHERE MAT_OD_CD = #{matOdCd}

UPDATE M_IN
SET MAT_SAMT = (MAT_SAMT - #{amt})
WHERE MAT_LOT = #{matLot}

UPDATE P_ODD
SET PODD_STS = 'PLN-STS3'
WHERE PODD_CD = #{poddCd}

mapper.select:
SELECT O.MAT_OD_CD, D.MTR_CD, C.MTR_NM, A.ACT_NM, I.MAT_IN_AMT, TO_CHAR(I.MAT_IN_DATE,'yyyy-MM-dd') AS "MAT_IN_DATE", I.MAT_LOT, TO_CHAR(I.MAT_EDATE,'yyyy-MM-dd') AS "MAT_EDATE"
FROM M_OD O
JOIN M_ODD D ON O.MAT_OD_CD = D.MAT_OD_CD
JOIN C_MTR C ON D.MTR_CD = C.MTR_CD
JOIN C_ACT A ON D.ACT_CD = A.ACT_CD
JOIN M_IN I ON I.MTR_CD = C.MTR_CD
D.MTR_CD = #{mtrCd}
AND A.ACT_NM = #{actNm}
AND O.MAT_OD_DD >= #{matInDate}
AND O.MAT_OD_DD <= #{matEdate}
ORDER BY O.MAT_OD_CD

SELECT I.MAT_LOT, C.MTR_CD, C.MTR_NM, A.ACT_NM, T.MAT_OUT_AMT, TO_CHAR(T.MAT_OUT_DATE,'yyyy-MM-dd') AS "MAT_OUT_DATE", TO_CHAR(I.MAT_EDATE, 'yyyy-MM-dd') AS "MAT_EDATE"
FROM M_IN I
JOIN C_MTR C ON I.MTR_CD = C.MTR_CD
JOIN C_ACT A ON A.ACT_CD = I.ACT_CD
JOIN M_OUT T ON C.MTR_CD = T.MTR_CD
C.MTR_CD = #{mtrCd}
AND A.ACT_NM = #{actNm}
AND T.MAT_OUT_DATE >= #{matInDate}
AND T.MAT_OUT_DATE <= #{matEdate}
ORDER BY I.MAT_LOT

SELECT I.MAT_LOT, C.MTR_CD, C.MTR_NM, A.ACT_NM, I.MAT_SAMT, TO_CHAR(I.MAT_EDATE, 'yyyy-MM-dd') AS "MAT_EDATE"
FROM M_IN I
JOIN C_MTR C ON I.MTR_CD = C.MTR_CD
JOIN C_ACT A ON A.ACT_CD = I.ACT_CD
C.MTR_CD = #{mtrCd}
AND A.ACT_NM = #{actNm}
AND I.MAT_EDATE >= #{matInDate}
AND I.MAT_EDATE <= #{matEdate}

mapper.update:
UPDATE M_CHK
SET ER_CD = 'ERR-MNP-006'
WHERE MAT_OD_CD = #{matOdCd}

mapper.select:
SELECT K.MAT_OD_CD, T.MTR_CD, FIND_MTR_NAME(T.MTR_CD) AS "MTR_NM", A.ACT_NM, T.MAT_RT_AMT, TO_CHAR(T.MAT_RT_DATE,'yyyy-MM-dd') AS "MAT_RT_DATE", TO_CHAR(K.MAT_CHK_DATE,'yyyy-MM-dd') AS "MAT_CHK_DATE", K.ER_INFO FROM M_RT T
JOIN M_CHK K ON T.MAT_CHK_CD = K.MAT_CHK_CD
JOIN C_ACT A ON A.ACT_CD = T.ACT_CD
T.MTR_CD = #{mtrCd}
AND A.ACT_NM = #{actNm}
AND MAT_RT_DATE >= #{matInDate}
AND MAT_RT_DATE <= #{matEdate}
AND T.MAT_RT_AMT > 0

mapper.update:
UPDATE C_MTR
SET MTR_SFAMT = #{mtrSfamt}
WHERE MTR_CD = #{mtrCd}
