mapper.select:
SELECT TO_CHAR(OD.OD_DATE,'yyyy-MM-dd') AS OD_DATE, OD.OD_CD, PRD.PRD_CD, PRD.PRD_NM, OD.OD_AMT, ACT.ACT_CD, ACT.ACT_NM, TO_CHAR(OD.DE_DATE,'yyyy-MM-dd') AS DE_DATE, find_code_name(OD.OD_STS) AS OD_STS
FROM B_OD OD JOIN C_PRD PRD ON PRD.PRD_CD = OD.PRD_CD
JOIN C_ACT ACT ON ACT.ACT_CD = OD.ACT_CD
WHERE OD_STS = #{odSts}
AND TRUNC(OD_DATE) BETWEEN #{startDate} AND #{endDate}
AND PRD.PRD_CD = #{prdCd}
AND ACT.ACT_CD = #{actCd}
ORDER BY OD_DATE

mapper.update:
UPDATE B_OD

PRD_CD = #{prdCd}, 
OD_AMT = #{odAmt}, 
ACT_CD = #{actCd}, 
DE_DATE = #{deDate}

WHERE OD_CD = #{odCd}

UPDATE B_OD
OD_STS = 'ORD-STS2'
WHERE OD_STS = (SELECT OD.OD_STS FROM B_OD OD, B_OUT OU WHERE OD.OD_CD = #{odCd}) AND OD_CD = #{odCd}

mapper.select:
SELECT OD.OD_CD, O.OUT_NO, P.PRD_CD, I.PRD_LOT, P.PRD_NM, A.ACT_NM, O.BAMT, TO_CHAR(O.BDATES,'yyyy-MM-dd') AS BDATES, TO_CHAR(I.POD_DATE,'yyyy-MM-dd') AS POD_DATE, TO_CHAR(I.EDATE,'yyyy-MM-dd') AS EDATE, O.OUT_ETC
FROM B_OUT O JOIN B_IN I ON O.PRD_LOT = I.PRD_LOT
JOIN C_ACT A ON A.ACT_CD = O.ACT_CD
JOIN C_PRD P ON P.PRD_CD = I.PRD_CD
JOIN B_OD OD ON OD.OD_CD = O.OD_CD
WHERE O.OUT_NO IS NOT NULL
AND TRUNC(I.POD_DATE) BETWEEN #{startDate} AND #{endDate}
AND P.PRD_CD = #{prdCd}
AND I.PRD_LOT = #{prdLot}
ORDER BY BDATES

mapper.update:
UPDATE B_IN I
SAMT = BIN_AMT - (SELECT SUM(BAMT) FROM B_OUT WHERE PRD_LOT = #{prdLot})
WHERE PRD_LOT = #{prdLot}

UPDATE B_IN I
SAMT = SAMT - (SELECT SUM(RE_AMT) FROM B_RT WHERE PRD_LOT = #{prdLot})
WHERE PRD_LOT = #{prdLot}

mapper.select:
SELECT TO_CHAR(POD.PODD_DATES,'yyyy-MM-dd') AS POD_DATE, I.PRD_LOT, P.PRD_CD, P.PRD_NM, I.SAMT
FROM B_IN I
JOIN P_ODD POD ON POD.PODD_CD = I.PODD_CD
JOIN C_PRD P ON P.PRD_CD = I.PRD_CD
WHERE P.PRD_CD = #{prdCd}
AND I.PRD_LOT = #{prdLot}

mapper.update:
	UPDATE B_IN

	SAMT = ABS(samt - #{agAmt}) 

	WHERE PRD_LOT = #{prdLot}

mapper.select:
SELECT TO_CHAR(D.RQT_DATE,'yyyy-MM-dd') AS RQT_DATE, D.RQT_NO, O.OD_CD, P.PRD_CD, P.PRD_NM, O.OD_AMT, (SELECT SUM(SAMT) FROM B_IN WHERE PRD_CD = P.PRD_CD GROUP BY PRD_CD) AS SAMT, D.RQT_AMT, TO_CHAR(O.DE_DATE,'yyyy-MM-dd') AS DE_DATE, find_code_name(D.RQT_STS) AS RQT_STS
FROM B_POD D
JOIN B_OD O ON D.OD_CD = O.OD_CD
JOIN C_PRD P ON P.PRD_CD = D.PRD_CD
WHERE P.PRD_CD = #{prdCd}
AND I.PRD_LOT = #{prdLot}

SELECT TO_CHAR(D.RQT_DATE,'yyyy-MM-dd') AS RQT_DATE, O.OD_CD, P.PRD_CD, P.PRD_NM, O.OD_AMT, (SELECT SUM(SAMT) FROM B_IN WHERE PRD_CD = P.PRD_CD GROUP BY PRD_CD) AS SAMT, D.RQT_AMT, TO_CHAR(O.DE_DATE,'yyyy-MM-dd') AS DE_DATE, find_code_name(D.RQT_STS) AS RQT_STS
FROM B_POD D JOIN B_OD O ON D.OD_CD = O.OD_CD
JOIN C_PRD P ON P.PRD_CD = D.PRD_CD
WHERE TRUNC(D.RQT_DATE) BETWEEN #{startDate} AND #{endDate}
AND RQT_STS = #{rqtSts}

mapper.update:
UPDATE B_POD

RQT_DATE = #{rqtDate},
RQT_AMT = #{rqtAmt}

WHERE RQT_NO = #{rqtNo}
